import './App.css';
import { useState } from 'react';

import Axios from 'axios';

function App() {
  const [name, setName] = useState("");
  const [salaire, setSalaire] = useState(0);

  const addEmploye = () =>{
    Axios.post("http://localhost:3000/create", {
      name:name,
      salaire:salaire,
    }).then(() => {
      console.log("ok");
    })
  };

  return (
    <div className="App">
      <div className='Information'> 

        <label> Nom employe</label>
        <input type="text"
          onChange={(event) =>{
            setName(event.target.value)
          }}>
        </input>

        <label> Salaire</label>
        <input type="number"
          onChange={(event) =>{
            setSalaire(event.target.value)
          }}>
        </input> 

        <button onClick={addEmploye}>Add</button>
      </div>
    </div>
  );
}


export default App;
