import { useState } from 'react';

import Axios from 'axios';


function ResForm() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [tel, setTel] = useState("")

  const addCustomer = () =>{
    Axios.post("http://localhost:3003/create", {
      name:name,
      email:email,
      tel:tel
    }).then(() => {
      console.log("ok");
    })
  };

  return (

<div>
  <div class="contain">

      <div class="wrapper">

        <div class="form">
          {/* <h4>GET IN TOUCH</h4> */}
          <h2 class="form-headline">My information</h2>
          {/* <form id="submit-form" action=""> */} <br /><br />
          <form onSubmit={addCustomer} >
            <p>
              <input id="name" class="form-input" type="text" name="user_name" placeholder="Your Name*" onChange={(event) =>{
                  setName(event.target.value)
                }}/>
              <small class="name-error"></small>
            </p>
            <p>
              <input id="email" class="form-input" type="email" name="user_email" placeholder="Your Email*"onChange={(event) =>{
                  setEmail(event.target.value)
                }}/>
              <small class="name-error"></small>
            </p>

            <p class="full-width">
              <textarea  minlength="20" id="message" cols="30" rows="7" name="message" placeholder="Your Message*" required></textarea>
              <small></small>
            </p>

            <input type="submit" value="Send" className="submit"/>
          </form>
        </div>

        <div class="contacts contact-wrapper">

          <ul>
            <li>We've driven online revenues of over <span class="highlight-text-grey">$2
                billion</span> for our clients. Ready to know
              how we can help you?</li>
            <span class="hightlight-contact-info">
              <li class="email-info"><i class="fa fa-envelope" aria-hidden="true"></i> info@demo.com</li>
              <li><i class="fa fa-phone" aria-hidden="true"></i> <span class="highlight-text">+91 11 1111 2900</span></li>
            </span>
          </ul>
        </div>
      </div>
      </div>
      </div>
    // </div>


  );
}
export default ResForm;
