import React from "react";

import { useRef } from 'react';
import emailjs from '@emailjs/browser';



export const ContactForm = () => {
  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm('service_nq9j0ac', 'template_c6flhub', form.current, {
        publicKey: 'cDEMDGDv06OsDuUwX',
      })
      .then(
        () => {
          console.log('SUCCESS!');
        },
        (error) => {
          console.log('FAILED...', error.text);
        },
      );
  };

  return (
<div>


  <section class="section-md text-center bg-image breadcrumbs-01">
            <div class="shell shell-fluid">
              <div class="range range-xs-center">
                <div class="cell-xs-12 cell-xl-11">
                  <h2 class="text-white">Contacts</h2>
                  <ul class="breadcrumbs-custom">
                    <li><a href="index.html">Home</a></li>
                    <li class="active">Contacts</li>
                  </ul>
                </div>
              </div>
            </div>
          </section>
    // ---------------------------
    <form ref={form} onSubmit={sendEmail}>
      <label>Name</label>
      <input type="text" name="user_name" />
      <label>Email</label>
      <input type="email" name="user_email" />
      <label>Message</label>
      <textarea name="message" />
      <input type="submit" value="Send" />
    </form>
    </div>
  );
};


export default ContactForm;
