  const [compteur, setCompteur] = useState(0);
  const [fruits, setFruits] = useState([
    { id:1, nom: "Abricot" },
    { id:2, nom: "Orange" },
    { id:3, nom: "Papaya" },

  ])

  // B comportement logique a suivre
  const handleCLick = () => {
    setCompteur(compteur + 1)
  }

  const handleDelete = (id) => {
    console.log(id)
    // 1 copier le state
    const fruitsCopy = [...fruits];
    console.log(fruitsCopy);

    // 2 manipuler la copie
    const fruitsCopyUpdated = fruitsCopy.filter( (fruit) => fruit.id !== id)

    // 3 appliquer le state deja manip au state initial 
    setFruits(fruitsCopyUpdated);
  }

  // C mretourne affichage
  return (
    <div>
      <h1>{compteur}</h1>
      <button onClick={handleCLick}>INCREMENTER</button>
      <ul>
        {fruits.map((fruit) => {
          return <li key={fruit.id}>{fruit.nom} <button onClick={() => {handleDelete(fruit.id) }}>X</button></li>
        })}
