import React from "react";
import './style/Banner.css';
import { Button } from '@material-ui/core';

import { useState } from "react";
import { Search } from "@material-ui/icons";
import DatePicker from "./DatePicker";

function Banner() {
    const [showSearch, setShowSearch] = useState(false);
    const [searchText, setSearchText] = useState('Search dates')

    return(
        <div className="banner">
            <div className="banner_search">
                <Button
                className="banner_searchButton"
                variant='outlined'
                onClick={ () => { setShowSearch(!showSearch); setSearchText('lol')}}> { searchText}
                </Button>


            </div>
            <div className="banner_info">
                <h1>Get out and stretch your imagination</h1>
                <h5>
                    Plan a different kind of gataway to uncover the hidden gems near you
                </h5>
                <Button variant='outlined'>
                    Explore
                </Button>
            </div>

            {showSearch && <DatePicker />}
        </div>
    )
}
export default Banner;
