                  <button class=" modal-btn-ok" onClick={() => setModalError(false)}>Try again</button>
                  </div>

                </div>
              </div>
  )}
           {/* <DurationContext.Provider value={{duration: ['T'] }}>
            <BookingForm />
           </DurationContext.Provider>
             */}
        </div>
    )
}

export default DatePicker;
