import React, { useState,useEffect } from 'react';
import './style/SearchResult.css';
import BookingForm from './BookingForm';
import { useNavigate } from 'react-router-dom';
import { withRouter } from 'react-router-dom';
import ResForm from './ResForm';

// import FavoriteBorderIcon from "@material-ui/icons/FavoriteBorder";
// import StarIcon from "@material-ui/icons/Star";

function SearchResult({
    id,
    img,
    location,
    title,
    description,
    star,
    price,
    total,
}) {

    const [roomID, setRoomID] = useState(0)
    // const [roomID2, setRoomID2] = useState(0)
    const navigate = useNavigate();
    // const book = () => {
    //     // Naviguer vers la nouvelle page avec l'ID de la chambre en tant que paramètre
    //     navigate(`/detail/${id}`);
    // };
    useEffect(() => {
        const roomID = localStorage.getItem('roomID');
        if (roomID) {
            setRoomID(parseInt(roomID)); 
        }
      }, []);
    // const [showForm, setShowForm] = useState(false); // État pour afficher ou masquer le formulaire
    const handleBookClick = () => {
        // setShowForm(true); 
        if(roomID === 0){
            navigate('/Panier')
            localStorage.setItem("roomID", id);
        }
        else{
            navigate('/ResForm')
            localStorage.setItem("roomID2", id);
        }



    };

     // Mise à jour de localStorage lorsque la durée change
    //  useEffect(() => {

    //   }, [duration]);


    return (
        <div className='searchResult'>
            <img src={img} alt="" />
            {/* <FavoriteBorderIcon className="searchResult__heart" /> */}

            <div className='searchResult__info'>
                <div className="searchResult__infoTop">
                    <p>{location}</p>
                    <h3>{title}</h3>
                    <p>____</p>
                    <p>{description}</p>
                </div>

                <div className="searchResult__infoBottom">
                    <div className="searchResult__stars">
                        {/* <StarIcon className="searchResult__star" /> */}
                        <p>
                            <strong>{star}</strong>
                        </p>
                    </div>
                    <div className='searchResults__price'>
                        <h2>{price}</h2>
                        <p>{total}</p>
                        {/* <p>{id}</p> */}
                        <button variant='outlined' onClick={handleBookClick}>
                        Book 
                    </button>

            {/* {showForm && <ResForm  onClose={() => setShowForm(false)} />} */}
                    </div>
                </div>
            </div>
        </div>
    )
}

export default SearchResult
